zz_context_t *zz_create(State*auxil) {
    return pcc_context__create(auxil);
}

int zz_parse(zz_context_t *ctx, Node**ret) {
    pcc_thunk_array_t thunks;
    pcc_thunk_array__init(ctx->auxil, &thunks, PCC_ARRAYSIZE);
    if (pcc_apply_rule(ctx, pcc_evaluate_rule_file, &thunks, ret))
        pcc_do_action(ctx, &thunks, ret);
    else
        PCC_ERROR(ctx->auxil);
    pcc_commit_buffer(ctx);
    pcc_thunk_array__term(ctx->auxil, &thunks);
    return pcc_refill_buffer(ctx, 1) >= 1;
}

void zz_destroy(zz_context_t *ctx) {
    pcc_context__destroy(ctx);
}


#include <sys/mman.h>
#include <sys/stat.h>
#include <fcntl.h>
#include <unistd.h>

int main(int argc, char ** argv) {

    const char *filepath = argv[1];
    int fd = open(filepath, O_RDONLY);
    if(fd < 0){
        fprintf(stderr, "\n\"%s\" could not open\n", filepath);
        return 1;
    }

    struct stat statbuf;
    int err = fstat(fd, &statbuf);
    if (err < 0) {
        fprintf(stderr, "\n\"%s\" could not fstat\n", filepath);
        return 2;
    }

    char *ptr = mmap(NULL,statbuf.st_size, PROT_READ,MAP_SHARED, fd,0);
    if(ptr == MAP_FAILED){
        fprintf(stderr, "\n\"%s\" could not mmap\n", filepath);
        close(fd);
        return 1;
    }
    close(fd);



    State s = {
        .filename   = filepath,
        .file = ptr,
        .size = statbuf.st_size,
        .line = 1,
    };

    zz_context_t *ctx = zz_create(&s);
    while (zz_parse(ctx, NULL));
    zz_destroy(ctx);


    err = munmap(ptr, statbuf.st_size);
    if(err != 0){
        printf("UnMapping Failed\n");
        return 1;
    }

    return 0;
}

